import {Component} from "react";

class Calculator extends Component {
    constructor(props) {
        super(props);
        this.state = {
            firstNumber: "",
            secondNumber: "",
            resultNumber: ""
        }
    }

    inputChange = (event) => {
        const {name, value} = event.target;
        this.setState({[name]: value});
    };
    sumFunction = (operator) => {
        const {firstNumber, secondNumber} = this.state;
        let result = 0;
        const number1 = parseInt(firstNumber);
        console.log(number1);
        const number2 = parseInt(secondNumber);
        if (isNaN(number1) || isFinite(number2)) {
            this.setState({resultNumber: "Invalid number"});
        } else {
            switch (operator) {
                case '+':
                    result = number1 + number2;
                    break;
                case '-':
                    result = number1 - number2;
                    break;
                case '*':
                    result = number1 * number2;
                    break;
                case '/':
                    result = number2 !== 0 ? number1 / number2 : 'Cannot divide by zero';
                    break;
                default:
                    result = 'Unknown operation';
            }
            this.setState({resultNumber: result});
        }
    }

    render() {
        return (
            <div className="calculator container form-control p-5">
                <div className="md-3">
                    <input name="firstNumber" value={this.state.firstNumber} className="first-number form-control m-2" onChange={(e) =>this.inputChange(e)}/>
                </div>
                <div className="md-3">
                    <input name="secondNumber" value={this.state.secondNumber} className="second-number form-control m-2" onChange={this.inputChange}/>
                </div>
                <label className="form-label form-control border-0">Result</label>
                <label className="form-label form-control border-0">{this.state.resultNumber}</label>
                <div className="form-control border-0 md-3">
                    <button onClick={() => this.sumFunction("+")} className="btn btn-dark m-1">+</button>
                    <button type="button" className="btn btn-dark m-1">-</button>
                    <button type="button" className="btn btn-dark m-1">x</button>
                    <button type="button" className="btn btn-dark m-1">/</button>
                </div>

            </div>
        )
    }

}


export default Calculator;
