import {useEffect, useState} from "react";

function Timer() {
    const [time, setTime] = useState(10);
    const [isRuning, setIsRuning] = useState(false);

    const handleStart = () =>{
        setIsRuning(true);
    }
    useEffect(() => {
        let timer;
        if (isRuning && time > 0) {
            timer = setInterval(() => {
                setTime((prevState) => prevState - 1)
            }, 1000)
        } else {
            if (time == 0 ){
                alert("Đã hết thời gian!")
            }
            setIsRuning(false);
        }
    }, []);
    return (
        <>
            <div className="form-control">
                <div>
                <label className="form-label">Đếm ngược từ {time}</label>
                </div>
                <button className="btn btn-info" type="button" onClick={handleStart}>Start</button>
            </div>
        </>
    )
}

export default Timer;
